mapper.select:
select u.id, u.username, u.emai, u.password, u.create_time createTime, u.state, u.sign, u.update_time updateTime, u.grade, u.expire, u.attr3 FROM users u where u.expire = 1
AND u.username = #{userQuery.username}
AND u.username = #{userQuery.email}
AND u.create_time > #{userQuery.createTimeFrom}
AND u.username = #{userQuery.createTimeTo}
order by u.create_time desc
