mapper.select:
select u.id, u.username, u.email, u.password, u.create_time createTime, u.state, u.sign, u.update_time updateTime, u.grade, u.expire, u.attr3 FROM users u where u.expire = 1
AND u.username like CONCAT('%',#{userQuery.username},'%')
AND u.email like CONCAT('%',#{userQuery.email},'%')
AND u.create_time > #{userQuery.createTimeFrom}
AND u.create_time < #{userQuery.createTimeTo}
order by u.create_time desc
